// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

interface ISupplyChain {
    function getLot(uint256 _lotId) external view returns (
        uint256 id,
        string memory title,
        string memory description,
        uint256 quantity,
        string memory unit,
        string memory origin,
        uint256 price,
        address creator,
        uint256 createdAt,
        bool exists
    );
    function getLotStepsCount(uint256 _lotId) external view returns (uint256);
    function getStep(uint256 _lotId, uint256 _stepIndex) external view returns (
        string memory description,
        address[] memory validators,
        address validatedBy,
        uint256 validatedAt,
        uint8 status
    );
}

contract EscrowPayment {

    ISupplyChain public supplyChain;

    struct Payment {
        uint256 lotId;
        address buyer;
        address seller;
        uint256 amount;
        uint256 createdAt;
        uint256 releasedAt;
        bool released;
    }

    mapping(uint256 => Payment) public payments;
    uint256[] public paymentLotIds;
    mapping(address => uint256) public totalReceived;
    mapping(address => uint256) public totalSpent;

    event PaymentDeposited(uint256 indexed lotId, address indexed buyer, address indexed seller, uint256 amount);
    event PaymentReleased(uint256 indexed lotId, address indexed seller, uint256 amount);
    event PaymentRefunded(uint256 indexed lotId, address indexed buyer, uint256 amount);

    constructor(address _supplyChainAddress) {
        supplyChain = ISupplyChain(_supplyChainAddress);
    }

    function depositPayment(uint256 _lotId) external payable {
        require(payments[_lotId].amount == 0, "Payment already exists for this lot");

        (,,,,, , uint256 price, address creator,, bool exists) = supplyChain.getLot(_lotId);
        require(exists, "Lot does not exist");
        require(creator != msg.sender, "Cannot buy your own lot");
        require(price > 0, "Lot has no price set");
        require(msg.value == price, "Payment must equal lot price");

        payments[_lotId] = Payment({
            lotId: _lotId,
            buyer: msg.sender,
            seller: creator,
            amount: msg.value,
            createdAt: block.timestamp,
            releasedAt: 0,
            released: false
        });

        paymentLotIds.push(_lotId);
        totalSpent[msg.sender] += msg.value;

        emit PaymentDeposited(_lotId, msg.sender, creator, msg.value);
    }

    function releasePayment(uint256 _lotId) external {
        Payment storage payment = payments[_lotId];
        require(payment.amount > 0, "No payment found");
        require(!payment.released, "Payment already released");
        require(_isLotCompleted(_lotId), "Lot not fully validated");

        payment.released = true;
        payment.releasedAt = block.timestamp;
        totalReceived[payment.seller] += payment.amount;

        (bool success, ) = payable(payment.seller).call{value: payment.amount}("");
        require(success, "Transfer failed");

        emit PaymentReleased(_lotId, payment.seller, payment.amount);
    }

    function refundPayment(uint256 _lotId) external {
        Payment storage payment = payments[_lotId];
        require(payment.amount > 0, "No payment found");
        require(!payment.released, "Payment already released");
        require(payment.buyer == msg.sender, "Only buyer can request refund");
        require(!_isLotCompleted(_lotId), "Lot already completed, cannot refund");

        uint256 amount = payment.amount;
        payment.amount = 0;
        payment.released = true;
        payment.releasedAt = block.timestamp;
        totalSpent[msg.sender] -= amount;

        (bool success, ) = payable(msg.sender).call{value: amount}("");
        require(success, "Refund failed");

        emit PaymentRefunded(_lotId, msg.sender, amount);
    }

    function _isLotCompleted(uint256 _lotId) internal view returns (bool) {
        uint256 stepsCount = supplyChain.getLotStepsCount(_lotId);
        if (stepsCount == 0) return false;

        for (uint256 i = 0; i < stepsCount; i++) {
            (,,,, uint8 status) = supplyChain.getStep(_lotId, i);
            if (status != 1) return false;
        }
        return true;
    }

    function getPayment(uint256 _lotId) external view returns (Payment memory) {
        return payments[_lotId];
    }

    function getPaymentsCount() external view returns (uint256) {
        return paymentLotIds.length;
    }

    function getPaymentByIndex(uint256 _index) external view returns (Payment memory) {
        require(_index < paymentLotIds.length, "Index out of bounds");
        return payments[paymentLotIds[_index]];
    }

    function isLotCompleted(uint256 _lotId) external view returns (bool) {
        return _isLotCompleted(_lotId);
    }

    function getContractBalance() external view returns (uint256) {
        return address(this).balance;
    }

    function getLotPrice(uint256 _lotId) external view returns (uint256) {
        (,,,,,,uint256 price,,,) = supplyChain.getLot(_lotId);
        return price;
    }
}

contracts/SupplyChain.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

contract SupplyChain {

    enum StepStatus { Pending, Completed }

    struct Step {
        string description;
        address[] validators;
        address validatedBy;
        uint256 validatedAt;
        StepStatus status;
    }

    struct Lot {
        uint256 id;
        string title;
        string description;
        uint256 quantity;
        string unit;
        string origin;
        uint256 price; // Price in wei
        address creator;
        uint256 createdAt;
        bool exists;
    }

    mapping(uint256 => Lot) public lots;
    mapping(uint256 => Step[]) public lotSteps;

    uint256 public nextLotId;

    event LotCreated(uint256 indexed lotId, string title, address indexed creator, uint256 price);
    event StepAdded(uint256 indexed lotId, uint256 stepIndex, string description);
    event StepValidated(uint256 indexed lotId, uint256 stepIndex, address validator);

    function createLot(
        string memory _title, 
        string memory _description,
        uint256 _quantity,
        string memory _unit,
        string memory _origin,
        uint256 _price,
        string[] memory _stepDescriptions, 
        address[][] memory _stepValidators
    ) public returns (uint256) {
        require(_stepDescriptions.length == _stepValidators.length, "Steps data mismatch");
        require(_quantity > 0, "Quantity must be greater than 0");

        uint256 lotId = nextLotId;

        lots[lotId] = Lot({
            id: lotId,
            title: _title,
            description: _description,
            quantity: _quantity,
            unit: _unit,
            origin: _origin,
            price: _price,
            creator: msg.sender,
            createdAt: block.timestamp,
            exists: true
        });

        for (uint256 i = 0; i < _stepDescriptions.length; i++) {
            Step memory newStep = Step({
                description: _stepDescriptions[i],
                validators: _stepValidators[i],
                validatedBy: address(0),
                validatedAt: 0,
                status: StepStatus.Pending
            });
            lotSteps[lotId].push(newStep);
            emit StepAdded(lotId, i, _stepDescriptions[i]);
        }

        emit LotCreated(lotId, _title, msg.sender, _price);
        nextLotId++;
        return lotId;
    }

    function validateStep(uint256 _lotId, uint256 _stepIndex) public {
        require(lots[_lotId].exists, "Lot does not exist");
        require(_stepIndex < lotSteps[_lotId].length, "Step does not exist");

        Step storage step = lotSteps[_lotId][_stepIndex];
        require(step.status == StepStatus.Pending, "Step already completed");

        if (_stepIndex > 0) {
            require(lotSteps[_lotId][_stepIndex - 1].status == StepStatus.Completed, "Previous step not completed");
        }

        bool isAuthorized = false;
        if (step.validators.length == 0) {
             isAuthorized = true;
        } else {
            for (uint256 i = 0; i < step.validators.length; i++) {
                if (step.validators[i] == msg.sender) {
                    isAuthorized = true;
                    break;
                }
            }
        }

        require(isAuthorized, "Not authorized to validate this step");

        step.status = StepStatus.Completed;
        step.validatedBy = msg.sender;
        step.validatedAt = block.timestamp;

        emit StepValidated(_lotId, _stepIndex, msg.sender);
    }

    function getLot(uint256 _lotId) public view returns (Lot memory) {
        return lots[_lotId];
    }

    function getLotStepsCount(uint256 _lotId) public view returns (uint256) {
        return lotSteps[_lotId].length;
    }

    function getStep(uint256 _lotId, uint256 _stepIndex) public view returns (
        string memory description,
        address[] memory validators,
        address validatedBy,
        uint256 validatedAt,
        StepStatus status
    ) {
        Step storage step = lotSteps[_lotId][_stepIndex];
        return (step.description, step.validators, step.validatedBy, step.validatedAt, step.status);
    }
}

