contracts/ContractDelivery.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

contract ContractDelivery {
    // Mapping from Lot ID to delivery status
    mapping(uint256 => bool) public isDelivered;
    mapping(uint256 => bool) public isCompliant;
    mapping(uint256 => address) public receiver;

    event DeliveryValidated(uint256 indexed lotId, bool compliant, address receiver);

    function validateDelivery(uint256 _lotId, bool _compliant) public {
        // Logic: receiver confirms delivery
        require(!isDelivered[_lotId], "Delivery already validated");

        isDelivered[_lotId] = true;
        isCompliant[_lotId] = _compliant;
        receiver[_lotId] = msg.sender;

        emit DeliveryValidated(_lotId, _compliant, msg.sender);
    }

    function checkDeliveryStatus(uint256 _lotId) public view returns (bool delivered, bool compliant) {
        return (isDelivered[_lotId], isCompliant[_lotId]);
    }
}

contracts/ContractLot.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

contract ContractLot {
    struct Lot {
        uint256 id;
        string origin;
        string productData; // Description, characteristics
        uint256 productionDate;
        address producer;
        bool exists;
    }

    mapping(uint256 => Lot) public lots;
    uint256 public nextLotId;

    event LotCreated(uint256 indexed lotId, address indexed producer, uint256 timestamp);

    function createLot(string memory _origin, string memory _productData) public returns (uint256) {
        uint256 lotId = nextLotId;
        lots[lotId] = Lot({
            id: lotId,
            origin: _origin,
            productData: _productData,
            productionDate: block.timestamp,
            producer: msg.sender,
            exists: true
        });

        emit LotCreated(lotId, msg.sender, block.timestamp);
        nextLotId++;
        return lotId;
    }

    function getLot(uint256 _lotId) public view returns (Lot memory) {
        require(lots[_lotId].exists, "Lot does not exist");
        return lots[_lotId];
    }
}

contracts/ContractPayment.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

interface IContractDelivery {
    function checkDeliveryStatus(uint256 _lotId) external view returns (bool delivered, bool compliant);
}

contract ContractPayment {
    IContractDelivery public contractDelivery;

    // Funds held in escrow for a Lot ID
    mapping(uint256 => uint256) public escrowAmount;
    mapping(uint256 => address) public payee; // Who gets the money (e.g. Supplier)
    mapping(uint256 => bool) public paid;

    event FundsDeposited(uint256 indexed lotId, uint256 amount, address payee);
    event PaymentReleased(uint256 indexed lotId, uint256 amount, address payee);

    constructor(address _contractDeliveryAddress) {
        contractDelivery = IContractDelivery(_contractDeliveryAddress);
    }

    // Buyer deposits funds for a specific Lot
    function deposit(uint256 _lotId, address _payee) public payable {
        require(msg.value > 0, "Amount must be greater than 0");
        require(escrowAmount[_lotId] == 0, "Funds already deposited for this Lot");

        escrowAmount[_lotId] = msg.value;
        payee[_lotId] = _payee;

        emit FundsDeposited(_lotId, msg.value, _payee);
    }

    // Release funds if Delivery is valid
    function releasePayment(uint256 _lotId) public {
        require(escrowAmount[_lotId] > 0, "No funds for this Lot");
        require(!paid[_lotId], "Already paid");

        // Check validation from Delivery Contract
        (bool delivered, bool compliant) = contractDelivery.checkDeliveryStatus(_lotId);
        require(delivered, "Delivery not validated yet");
        require(compliant, "Delivery not compliant, payment held");

        uint256 amount = escrowAmount[_lotId];
        address recipient = payee[_lotId];

        paid[_lotId] = true;
        escrowAmount[_lotId] = 0;

        (bool sent, ) = recipient.call{value: amount}("");
        require(sent, "Failed to send Ether");

        emit PaymentReleased(_lotId, amount, recipient);
    }
}

contracts/ContractTransport.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

contract ContractTransport {
    struct Step {
        string location;
        string status; // e.g. "In Transit", "Arrived"
        uint256 temperature; // integer representation
        uint256 timestamp;
        address handler;
    }

    // Mapping from Lot ID to list of Steps
    mapping(uint256 => Step[]) public trackingHistory;

    event StepAdded(uint256 indexed lotId, string location, string status, address handler);

    function addStep(uint256 _lotId, string memory _location, string memory _status, uint256 _temperature) public {
        // In a real app, we would verify _lotId exists in ContractLot

        Step memory newStep = Step({
            location: _location,
            status: _status,
            temperature: _temperature,
            timestamp: block.timestamp,
            handler: msg.sender
        });

        trackingHistory[_lotId].push(newStep);
        emit StepAdded(_lotId, _location, _status, msg.sender);
    }

    function getHistory(uint256 _lotId) public view returns (Step[] memory) {
        return trackingHistory[_lotId];
    }
}

